INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '  n = ((n >>  1) & 0x55555555) | ((n <<  1) & 0xaaaaaaaa):<br>   n = ((n >>  2) & 0x33333333) | ((n <<  2) & 0xcccccccc):<br>   n = ((n >>  4) & 0x0f0f0f0f) | ((n <<  4) & 0xf0f0f0f0):<br>   n = ((n >>  8) & 0x00ff00ff) | ((n <<  8) & 0xff00ff00):<br>   n = ((n >> 16) & 0x0000ffff) | ((n << 16) & 0xffff0000):<br><br>&nbsp;-- C code which reverses the bits in a word.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'If you give Congress a chance to vote on both sides of an issue, it&nbsp;will always do it.<br>&nbsp;-- Les Aspin, D., Wisconsin');
