INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"The only thing we learn from history is that we learn nothing from&nbsp;history."<br>&nbsp;-- Hegel&nbsp;&nbsp;"I know guys can''t learn from yesterday ... Hegel must be taking the&nbsp;long view."<br>&nbsp;-- John Brunner, "Stand on Zanzibar"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Three great scientific theories of the structure of the universe are&nbsp;the molecular, the corpuscular and the atomic.  A fourth affirms, with&nbsp;Haeckel, the condensation or precipitation of matter from ether --&nbsp;whose existence is proved by the condensation or precipitation ... A&nbsp;fifth theory is held by idiots, but it is doubtful if they know any&nbsp;more about the matter than the others.<br>&nbsp;-- Ambrose Bierce, "The Devil''s Dictionary"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Duct tape is like the force.  It has a light side, and a dark side, and&nbsp;it holds the universe together ...<br>&nbsp;-- Carl Zwanzig');
