INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"I appreciate the fact that this draft was done in haste, but some of&nbsp;the sentences that you are sending out in the world to do your work for&nbsp;you are loitering in taverns or asleep beside the highway."<br>&nbsp;-- Dr. Dwight Van de Vate, Professor of Philosophy,<br>&nbsp;   University of Tennessee at Knoxville');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'People will accept your ideas much more readily if you tell them that&nbsp;Benjamin Franklin said it first.');
