INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The herd instinct among economists makes sheep look like independent&nbsp;thinkers.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'A penny saved is ridiculous.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'I realize that the MX missile is none of our concern.  I realize that&nbsp;the whole point of living in a democracy is that we pay professional&nbsp;congresspersons to concern themselves with things like the MX missile&nbsp;so we can be free to concern ourselves with getting hold of the&nbsp;plumber.<br>&nbsp;But from time to time, I feel I must address major public issues such&nbsp;as this, because in a free and open society, where the very future of&nbsp;the world hinges on decisions made by our elected leaders, you never&nbsp;win large cash journalism awards if you stick to the topics I usually&nbsp;write about, such as nose-picking.<br>&nbsp;-- Dave Barry, "At Last, the Ultimate Deterrent Against<br>&nbsp;   Political Fallout"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The opposite of a profound truth may well be another profound truth.<br>&nbsp;-- Bohr');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"I thought you were trying to get into shape."&nbsp;"I am. The shape I''ve selected is a triangle."');
