INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The President publicly apologized today to all those offended by his&nbsp;brother''s remark, "There''s more Arabs in this country than there is&nbsp;Jews!".  Those offended include Arabs, Jews, and English teachers.<br>&nbsp;-- Baltimore, Channel 11 News, on Jimmy Carter');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Blessed are the young for they shall inherit the national debt.');
