INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The marvels of today''s modern technology include the development of a&nbsp;soda can, when discarded will last forever ... and a $7,000 car which&nbsp;when properly cared for will rust out in two or three years.');
