INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Vanilla, adj.:<br>Ordinary flavor, standard.  See FLAVOR.  When used of food,<br>very often does not mean that the food is flavored with vanilla&nbsp;extract!  For example, "vanilla-flavored won ton soup" (or simply&nbsp;"vanilla won ton soup") means ordinary won ton soup, as opposed to hot&nbsp;and sour won ton soup.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Buzz off, Banana Nose; Relieve mine eyes&nbsp;Of hateful soreness, purge mine ears of corn:<br>Less dear than army ants in apple pies&nbsp;Art thou, old prune-face, with thy chestnuts worn,<br>Dropt from thy peeling lips like lousy fruit:<br>Like honeybees upon the perfum''d rose&nbsp;They suck, and like the double-breasted suit&nbsp;Are out of date; therefore, Banana Nose,<br>Go fly a kite, thy welcome''s overstayed:<br>And stem the produce of thy waspish wits:<br>Thy logick, like thy locks, is disarrayed:<br>Thy cheer, like thy complexion, is the pits.<br>Be off, I say; go bug somebody new,<br>Scram, beat it, get thee hence, and nuts to you.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Waste not, get your budget cut next year.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Bennett''s Laws of Horticulture:<br>(1) Houses are for people to live in.<br>(2) Gardens are for plants to live in.<br>(3) There is no such thing as a houseplant.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;"You know, it''s at times like this when I''m trapped in a Vogon&nbsp;airlock with a man from Betelgeuse and about to die of asphyxiation in&nbsp;deep space that I really wish I''d listened to what my mother told me&nbsp;when I was young!"<br>"Why, what did she tell you?"<br>"I don''t know, I didn''t listen!"<br>&nbsp;-- Douglas Adams, "Hitchhiker''s Guide to the Galaxy"');
