INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'History, n.:<br>Papa Hegel he say that all we learn from history is that we&nbsp;learn nothing from history.  I know people who can''t even learn from&nbsp;what happened this morning.  Hegel must have been taking the long&nbsp;view.<br>&nbsp;-- Chad C. Mulligan, "The Hipcrime Vocab"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'All the taxes paid over a lifetime by the average American are spent by&nbsp;the government in less than a second.<br>&nbsp;-- Jim Fiebig');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Yesterday I was a dog.  Today I''m a dog.  Tomorrow I''ll probably still&nbsp;be a dog. Sigh!  There''s so little hope for advancement.<br>&nbsp;-- Snoopy');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Celestial navigation is based on the premise that the Earth is the&nbsp;center of the universe.  The premise is wrong, but the navigation&nbsp;works.  An incorrect model can be a useful tool.<br>&nbsp;-- Kelvin Throop III');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Stay away from hurricanes for a while.');
