INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Heller''s Law:<br>The first myth of management is that it exists.<br>&nbsp;Johnson''s Corollary:<br>Nobody really knows what is going on anywhere within the&nbsp;organization.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'A new koan:<br><br>If you have some ice cream, I will give it to you.<br><br>If you have no ice cream, I will take it away from you.<br>&nbsp;It is an ice cream koan.');
