INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Proposed Additions to the PDP-11 Instruction Set:<br>&nbsp;BBW&nbsp;Branch Both Ways&nbsp;BEW&nbsp;Branch Either Way&nbsp;BBBF&nbsp;Branch on Bit Bucket Full&nbsp;BH&nbsp;Branch and Hang&nbsp;BMR&nbsp;Branch Multiple Registers&nbsp;BOB&nbsp;Branch On Bug&nbsp;BPO&nbsp;Branch on Power Off&nbsp;BST&nbsp;Backspace and Stretch Tape&nbsp;CDS&nbsp;Condense and Destroy System&nbsp;CLBR&nbsp;Clobber Register&nbsp;CLBRI&nbsp;Clobber Register Immediately&nbsp;CM&nbsp;Circulate Memory&nbsp;CMFRM&nbsp;Come From -- essential for truly structured programming&nbsp;CPPR&nbsp;Crumple Printer Paper and Rip&nbsp;CRN&nbsp;Convert to Roman Numerals');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'In Denver it is unlawful to lend your vacuum cleaner to your next-door&nbsp;neighbor.');
