INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'You may be recognized soon.  Hide.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'If you pick up a starving dog and make him prosperous, he will not bite&nbsp;you.  This is the principal difference between a dog and a man.<br>&nbsp;-- Mark Twain');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"See - the thing is - I''m an absolutist.  I mean, kind of ... in a way ..."');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The primary purpose of the DATA statement is to give names to&nbsp;constants; instead of referring to pi as 3.141592653589793 at every&nbsp;appearance, the variable PI can be given that value with a DATA&nbsp;statement and used instead of the longer form of the constant.  This&nbsp;also simplifies modifying the program, should the value of pi change.<br>&nbsp;-- FORTRAN manual for Xerox Computers');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'I have a simple philosophy:<br><br>Fill what''s empty.<br>Empty what''s full.<br>Scratch where it itches.<br>&nbsp;-- A. R. Longworth');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'California, n.:<br>From Latin "calor", meaning "heat" (as in English "calorie" or&nbsp;Spanish "caliente"); and "fornia''" for "sexual intercourse" or&nbsp;"fornication."  Hence: Tierra de California, "the land of hot sex."<br>&nbsp;-- Ed Moran');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'God made the world in six days, and was arrested on the seventh.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Nondeterminism means never having to say you are wrong.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Twenty Percent of Zero is Better than Nothing.<br>&nbsp;-- Walt Kelly');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Of course there''s no reason for it, it''s just our policy.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'George Washington was first in war, first in peace -- and the first to&nbsp;have his birthday juggled to make a long weekend.<br>&nbsp;-- Ashley Cooper');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Burn''s Hog Weighing Method:<br>(1) Get a perfectly symmetrical plank and balance it across a<br>    sawhorse.<br>(2) Put the hog on one end of the plank.<br>(3) Pile rocks on the other end until the plank is again<br>    perfectly balanced.<br>(4) Carefully guess the weight of the rocks.<br>&nbsp;-- Robert Burns');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Slowly and surely the unix crept up on the Nintendo user ...');
