INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'For some reason, this fortune reminds everyone of Marvin Zelkowitz.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"I am convinced that the manufacturers of carpet odor removing powder&nbsp;have included encapsulated time released cat urine in their products.<br>This technology must be what prevented its distribution during my mom''s&nbsp;reign.  My carpet smells like piss, and I don''t have a cat.  Better go&nbsp;by some more."<br>&nbsp;-- timw@zeb.USWest.COM');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"I stayed up all night playing poker with tarot cards.  I got a full&nbsp;house and four people died."<br>&nbsp;-- Steven Wright');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'You look like a million dollars.  All green and wrinkled.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'For every complex problem, there is a solution that is simple, neat,<br>and wrong.<br>&nbsp;-- H. L. Mencken');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'In any formula, constants (especially those obtained from handbooks)<br>are to be treated as variables.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Give me the Luxuries, and the Hell with the Necessities!');
