INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Surprise!  You are the lucky winner of random I.R.S. Audit!  Just type&nbsp;in your name and social security number.  Please remember that leaving&nbsp;the room is punishable under law:<br>&nbsp;Name&nbsp;#');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Don''t put off for tomorrow what you can do today, because if you enjoy&nbsp;it today you can do it again tomorrow.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'You can only live once, but if you do it right, once is enough.');
