INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'FORTUNE DISCUSSES THE OBSCURE FILMS!&nbsp;#6&nbsp;&nbsp;RAZORBACK:&nbsp;&nbsp;&nbsp;Paul Harbride, 1984, 2 hours 25 min.<br>One of the great Australian films of the early 1980''s, and<br>arguably the best movie ever made about a large, man-eating<br>hog.  Some violence.  With Gregory Harrison.');
