INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'It is Mr. Mellon''s credo that $200,000,000 can do no wrong.  Our&nbsp;offense consists in doubting it.<br>&nbsp;-- Justice Robert H. Jackson');
