INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The rhino is a homely beast,<br>For human eyes he''s not a feast.<br>Farewell, farewell, you old rhinoceros,<br>I''ll stare at something less prepoceros.<br>&nbsp;-- Ogden Nash');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"These are DARK TIMES for all mankind''s HIGHEST VALUES!"&nbsp;"These are DARK TIMES for FREEDOM and PROSPERITY!"&nbsp;"These are GREAT TIMES to put your money on BAD GUY to kick the CRAP&nbsp;out of MEGATON MAN!"');
