INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'I suggest you locate your hot tub outside your house, so it won''t do&nbsp;too much damage if it catches fire or explodes.  First you decide which&nbsp;direction your hot tub should face for maximum solar energy.  After&nbsp;much trial and error, I have found that the best direction for a hot&nbsp;tub to face is up.<br>&nbsp;-- Dave Barry, "The Taming of the Screw"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Moon, n.:<br>1. A celestial object whose phase is very important to&nbsp;hackers.  See PHASE OF THE MOON.  2. Dave Moon (MOON@MC).');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'You can take all the impact that science considerations have on funding&nbsp;decisions at NASA, put them in the navel of a flea, and have room left&nbsp;over for a caraway seed and Tony Calio''s heart.<br>&nbsp;-- F. Allen');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Nasrudin was carrying home a piece of liver and the recipe for liver&nbsp;pie.  Suddenly a bird of prey swooped down and snatched the piece of&nbsp;meat from his hand.  As the bird flew off, Nasrudin called after it,<br>"Foolish bird!  You have the liver, but what can you do with it without&nbsp;the recipe?"');
