INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Once again, we come to the Holiday Season, a deeply religious time that&nbsp;each of us observes, in his own way, by going to the mall of his&nbsp;choice.<br>&nbsp;In the old days, it was not called the Holiday Season; the Christians&nbsp;called it "Christmas" and went to church; the Jews called it "Hanukka"&nbsp;and went to synagogue; the atheists went to parties and drank.  People&nbsp;passing each other on the street would say "Merry Christmas!" or "Happy&nbsp;Hanukka!" or (to the atheists) "Look out for the wall!"<br>&nbsp;-- Dave Barry, "Christmas Shopping: A Survivor''s Guide"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'You worry too much about your job.  Stop it.  You''re not paid enough to&nbsp;worry.');
