INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Nine megs for the secretaries fair,<br>Seven megs for the hackers scarce,<br>Five megs for the grads in smoky lairs,<br>Three megs for system source:<br>&nbsp;One disk to rule them all,<br>One disk to bind them,<br>One disk to hold the files&nbsp;And in the darkness grind ''em.');
