INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Don''t hate yourself in the morning -- sleep till noon.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"I know the answer!  The answer lies within the heart of all mankind!&nbsp;The answer is twelve?  I think I''m in the wrong building."<br>&nbsp;-- Charles Schulz');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"If a team is in a positive frame of mind, it will have a good&nbsp;attitude.  If it has a good attitude, it will make a commitment to&nbsp;playing the game right.  If it plays the game right, it will win --&nbsp;unless, of course, it doesn''t have enough talent to win, and no manager&nbsp;can make goose-liver pate out of goose feathers, so why worry?"<br>&nbsp;-- Sparky Anderson');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'One good reason why computers can do more work than people is that they&nbsp;never have to stop and answer the phone.');
