INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'A witty saying proves nothing, but saying something pointless gets&nbsp;people''s attention.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;After his Ignoble Disgrace, Satan was being expelled from&nbsp;Heaven.  As he passed through the Gates, he paused a moment in thought,<br>and turned to God and said, "A new creature called Man, I hear, is soon&nbsp;to be created."<br>"This is true," He replied.<br>"He will need laws," said the Demon slyly.<br>"What!  You, his appointed Enemy for all Time!  You ask for the&nbsp;right to make his laws?"<br>"Oh, no!"  Satan replied, "I ask only that he be allowed to&nbsp;make his own."<br>It was so granted.<br>&nbsp;-- Ambrose Bierce, "The Devil''s Dictionary"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'And I heard Jeff exclaim,<br>As they strolled out of sight,<br>"Merry Christmas to all --&nbsp;You take credit cards, right?"<br>&nbsp;-- "Outsiders" comic');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The universe does not have laws -- it has habits, and habits can be&nbsp;broken.');
