INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Do not believe in miracles -- rely on them.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Valerie: Aww, Tom, you''re going maudlin on me ...<br>Tom:&nbsp; I reserve the right to wax maudlin as I wane eloquent ...<br>&nbsp;-- Tom Chapin');
