INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Lizzie Borden took an axe,<br>And plunged it deep into the VAX:<br>Don''t you envy people who&nbsp;Do all the things ___^H^H^HYOU want to do?');
