INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '&nbsp;&nbsp;&nbsp;ACHTUNG!!!&nbsp;&nbsp;Das machine is nicht fur gefingerpoken und mittengrabben.  Ist easy&nbsp;schnappen der springenwerk, blowenfusen und corkenpoppen mit&nbsp;spitzensparken.  Ist nicht fur gewerken by das dummkopfen.  Das&nbsp;rubbernecken sightseeren keepen hands in das pockets.  Relaxen und&nbsp;vatch das blinkenlights!!!');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Passionate hatred can give meaning and purpose to an empty life.<br>&nbsp;-- Eric Hoffer');
