INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Your lucky color has faded.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Bypasses are devices that allow some people to dash from point A to&nbsp;point B very fast while other people dash from point B to point A very&nbsp;fast.  People living at point C, being a point directly in between, are&nbsp;often given to wonder what''s so great about point A that so many people&nbsp;from point B are so keen to get there and what''s so great about point B&nbsp;that so many people from point A are so keen to get _____^H^H^H^H^Hthere.  They often&nbsp;wish that people would just once and for all work out where the hell&nbsp;they wanted to be.<br>&nbsp;-- Douglas Adams, "The Hitchhiker''s Guide to the Galaxy"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'You can create your own opportunities this week.  Blackmail a senior&nbsp;executive.');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Bizarreness is the essence of the exotic');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, '"Why are we importing all these highbrow plays like `Amadeus''?  I could&nbsp;have told you Mozart was a jerk for nothing."<br>&nbsp;-- Ian Shoales');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'A recent study has found that concentrating on difficult off-screen&nbsp;objects, such as the faces of loved ones, causes eye strain in computer&nbsp;scientists.  Researchers into the phenomenon cite the added&nbsp;concentration needed to "make sense" of such unnatural three&nbsp;dimensional objects ...');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'When we understand knowledge-based systems, it will be as before --&nbsp;except our fingertips will have been singed.<br>&nbsp;-- Epigrams in Programming, ACM SIGPLAN Sept. 1982');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Be free and open and breezy!  Enjoy!  Things won''t get any better so&nbsp;get used to it.');
