INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'The Preacher, the Politician, the Teacher,<br>Were each of them once a kiddie.<br>A child, indeed, is a wonderful creature.<br>Do I want one?  God Forbiddie!<br>&nbsp;-- Ogden Nash');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Mistakes are often the stepping stones to utter failure.');
