INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Hier liegt ein Mann ganz obnegleich:<br>Im Leibe dick, an Suden reich.<br>Wir haben ihn in das Grab gesteckt,&nbsp;Here lies a man with sundry flaws&nbsp;Weil es uns dunkt er sei verreckt.&nbsp;And numerous Sins upon his head;<br>&nbsp;&nbsp;&nbsp;&nbsp;We buried him today because<br>&nbsp;&nbsp;&nbsp;&nbsp;As far as we can tell, he''s dead.<br>&nbsp;-- PDQ Bach''s epitaph, as requested by his cousin Betty<br>&nbsp;   Sue Bach and written by the local doggerel catcher;<br>&nbsp;   "The Definitive Biography of PDQ Bach", Peter<br>&nbsp;   Schickele');
