INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Ah, but the choice of dreams to live, &nbsp;there''s the rub.<br>&nbsp;For all dreams are not equal,<br>some exit to nightmare&nbsp;most end with the dreamer&nbsp;&nbsp;But at least one must be lived ... and died.');
