INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'MOCK APPLE PIE (No Apples Needed)<br>&nbsp;  Pastry to two crust 9-inch pie&nbsp;36 RITZ Crackers&nbsp;2 cups water&nbsp;&nbsp;&nbsp;&nbsp; 2 cups sugar&nbsp;2 teaspoons cream of tartar&nbsp;&nbsp; 2 tablespoons lemon juice&nbsp;  Grated rind of one lemon&nbsp;&nbsp;   Butter or margarine&nbsp;  Cinnamon&nbsp;&nbsp;Roll out bottom crust of pastry and fit into 9-inch pie plate.  Break&nbsp;RITZ Crackers coarsely into pastry-lined plate.  Combine water, sugar&nbsp;and cream of tartar in saucepan, boil gently for 15 minutes.  Add lemon&nbsp;juice and rind.  Cool.  Pour this syrup over Crackers, dot generously&nbsp;with butter or margarine and sprinkle with cinnamon.  Cover with top&nbsp;crust.  Trim and flute edges together.  Cut slits in top crust to let&nbsp;steam escape.  Bake in a hot oven (425 F) 30 to 35 minutes, until crust&nbsp;is crisp and golden.  Serve warm.  Cut into 6 to 8 slices.<br>&nbsp;-- Found lurking on a Ritz Crackers box');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Brain, v. [as in "to brain"]:<br>To rebuke bluntly, but not pointedly; to dispel a source of&nbsp;error in an opponent.<br>&nbsp;-- Ambrose Bierce, "The Devil''s Dictionary"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'After a few boring years, socially meaningful rock ''n'' roll died out.<br>It was replaced by disco, which offers no guidance to any form of life&nbsp;more advanced than the lichen family.<br>&nbsp;-- Dave Barry, "Kids Today: They Don''t Know Dum Diddly<br>&nbsp;   Do"');
