INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'Honorable, adj.:<br>Afflicted with an impediment in one''s reach.  In legislative&nbsp;bodies, it is customary to mention all members as honorable; as, "the&nbsp;honorable gentleman is a scurvy cur."<br>&nbsp;-- Ambrose Bierce, "The Devil''s Dictionary"');
INSERT INTO Quote (Guid, LegacyID, Data) VALUES (randomblob(16), null, 'With every passing hour our solar system comes forty-three thousand&nbsp;miles closer to globular cluster M13 in the constellation Hercules, and&nbsp;still there are some misfits who continue to insist that there is no&nbsp;such thing as progress.<br>&nbsp;-- Ransom K. Ferm');
